#include <QtTest>
#include <QPixmap>
#include <QGraphicsScene>
#include <QMenu>
#include <QPainter>
#include <QStyleOptionGraphicsItem>
#include <QWidget>
#include "../diagramitem.h"
#include "../diagramtextitem.h"

class TestDiagramItem : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testConstructor();
    void testBoundingRect();
    void testSetBrush();
    void testSetFixedSize();
    void testRotationAngle();
    void testDiagramType();
    void testTextItemAssociation();
    void testFlags();
    void testPaint();

private:
    QGraphicsScene *scene;
    QMenu *contextMenu;
    DiagramItem *item;
};

void TestDiagramItem::initTestCase()
{
    scene = new QGraphicsScene();
    contextMenu = new QMenu();
    item = new DiagramItem(DiagramItem::Step, contextMenu);
    scene->addItem(item);
}

void TestDiagramItem::cleanupTestCase()
{
    delete scene;
    delete contextMenu;
    // item is owned by scene and will be deleted
}

void TestDiagramItem::testConstructor()
{
    QVERIFY(item != nullptr);
    QCOMPARE(item->diagramType(), DiagramItem::Step);
    QVERIFY(item->textItem != nullptr);
    QCOMPARE(item->textItem->toPlainText(), QString("请输入"));
}

void TestDiagramItem::testBoundingRect()
{
    QRectF rect = item->boundingRect();
    QVERIFY(rect.isValid());
    QVERIFY(rect.width() > 0);
    QVERIFY(rect.height() > 0);
}

void TestDiagramItem::testSetBrush()
{
    QColor newColor(Qt::red);
    item->setBrush(newColor);
    // Since m_color is private, we can't directly verify.
    // This test ensures the function call doesn't crash.
    QVERIFY(true);
}

void TestDiagramItem::testSetFixedSize()
{
    QSizeF newSize(200, 150);
    item->setFixedSize(newSize);
    QRectF rect = item->boundingRect();
    // Bounding rect includes rotation and border, so exact size match is not guaranteed.
    // We just verify the call doesn't crash.
    QVERIFY(rect.isValid());
}

void TestDiagramItem::testRotationAngle()
{
    qreal initialAngle = item->rotationAngle();
    QCOMPARE(initialAngle, 0.0);

    item->setRotationAngle(45.0);
    QCOMPARE(item->rotationAngle(), 45.0);

    item->setRotationAngle(-30.0);
    QCOMPARE(item->rotationAngle(), -30.0);
}

void TestDiagramItem::testDiagramType()
{
    QCOMPARE(item->diagramType(), DiagramItem::Step);
    // Test that the type is correctly stored and returned.
}

void TestDiagramItem::testTextItemAssociation()
{
    DiagramTextItem *text = item->textItem;
    QVERIFY(text != nullptr);
    QVERIFY(text->parentItem() == item);
    QCOMPARE(text->toPlainText(), QString("请输入"));
}

void TestDiagramItem::testFlags()
{
    QVERIFY(item->flags() & QGraphicsItem::ItemIsFocusable);
    QVERIFY(item->flags() & QGraphicsItem::ItemIsSelectable);
    QVERIFY(item->flags() & QGraphicsItem::ItemSendsGeometryChanges);
    QVERIFY(item->flags() & QGraphicsItem::ItemIsMovable);
}

void TestDiagramItem::testPaint()
{
    // Create a pixmap to paint onto
    QPixmap pixmap(300, 300);
    pixmap.fill(Qt::transparent);
    QPainter painter(&pixmap);
    QStyleOptionGraphicsItem option;
    option.state = QStyle::State_None;

    // Ensure painting doesn't crash
    item->paint(&painter, &option, nullptr);
    QVERIFY(true);
}

QTEST_MAIN(TestDiagramItem)
