#include <QtTest>
#include <QGraphicsScene>
#include <QGraphicsRectItem>
#include "deletecommand.h"

class TestDeleteCommand : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testConstructor();
    void testRedoRemovesItem();
    void testUndoAddsItem();
    void testUndoRestoresPosition();
    void testCommandText();

private:
    QGraphicsScene *scene;
    QGraphicsRectItem *testItem;
    DeleteCommand *command;
};

void TestDeleteCommand::initTestCase()
{
    scene = new QGraphicsScene();
    testItem = new QGraphicsRectItem(0, 0, 100, 50);
    testItem->setPos(50, 30);
    scene->addItem(testItem);
    command = new DeleteCommand(testItem, scene);
}

void TestDeleteCommand::cleanupTestCase()
{
    delete command;
    delete scene;
    // testItem is owned by scene or command, do not delete separately
}

void TestDeleteCommand::testConstructor()
{
    QVERIFY(command != nullptr);
    QCOMPARE(command->scene(), scene);
    // The item pointer should be stored
    QVERIFY(command->item() == testItem);
}

void TestDeleteCommand::testRedoRemovesItem()
{
    // Ensure item is in scene initially
    QVERIFY(testItem->scene() == scene);
    command->redo();
    QVERIFY(testItem->scene() == nullptr);
}

void TestDeleteCommand::testUndoAddsItem()
{
    // After redo, item is removed
    command->redo();
    QVERIFY(testItem->scene() == nullptr);
    command->undo();
    QVERIFY(testItem->scene() == scene);
}

void TestDeleteCommand::testUndoRestoresPosition()
{
    QPointF originalPos = testItem->pos();
    command->redo();
    command->undo();
    QCOMPARE(testItem->pos(), originalPos);
}

void TestDeleteCommand::testCommandText()
{
    // Default text should be set (inherited from QUndoCommand)
    QVERIFY(!command->text().isEmpty());
}

QTEST_MAIN(TestDeleteCommand)
