#include <QtTest>
#include <QGraphicsScene>
#include <QGraphicsRectItem>
#include "../deletecommand.h"

class TestDeleteCommand : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testConstructor();
    void testUndoRedo();

private:
    QGraphicsScene *scene;
    QGraphicsItem *testItem;
};

void TestDeleteCommand::initTestCase()
{
    scene = new QGraphicsScene();
    testItem = new QGraphicsRectItem(0, 0, 100, 100);
    scene->addItem(testItem);
}

void TestDeleteCommand::cleanupTestCase()
{
    delete scene; // 会删除 testItem
}

void TestDeleteCommand::testConstructor()
{
    DeleteCommand cmd(testItem, scene);
    QCOMPARE(cmd.m_item, testItem);
    QCOMPARE(cmd.m_scene, scene);
    QVERIFY(!cmd.m_itemPos.isNull());
    QVERIFY(cmd.m_itemSize.isValid());
}

void TestDeleteCommand::testUndoRedo()
{
    // 初始状态：item 在场景中
    QVERIFY(scene->items().contains(testItem));

    DeleteCommand cmd(testItem, scene);

    // 执行 redo：移除 item
    cmd.redo();
    QVERIFY(!scene->items().contains(testItem));

    // 执行 undo：恢复 item
    cmd.undo();
    QVERIFY(scene->items().contains(testItem));

    // 再次 redo
    cmd.redo();
    QVERIFY(!scene->items().contains(testItem));
}

QTEST_MAIN(TestDeleteCommand)
