#include <QtTest>
#include <QGraphicsScene>
#include <QMenu>
#include "../diagramitem.h"
#include "../diagrampath.h"

class TestDiagramPath : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testConstructor();
    void testQuadFunction();
    void testGetStartEndItem();

private:
    QGraphicsScene *scene;
    QMenu *dummyMenu;
    DiagramItem *startItem;
    DiagramItem *endItem;
};

void TestDiagramPath::initTestCase()
{
    scene = new QGraphicsScene();
    dummyMenu = new QMenu();
    startItem = new DiagramItem(DiagramItem::Step, dummyMenu);
    endItem = new DiagramItem(DiagramItem::Step, dummyMenu);
    scene->addItem(startItem);
    scene->addItem(endItem);
    startItem->setPos(0, 0);
    endItem->setPos(200, 200);
}

void TestDiagramPath::cleanupTestCase()
{
    delete scene; // 会删除 items
    delete dummyMenu;
}

void TestDiagramPath::testConstructor()
{
    DiagramPath path(startItem, endItem, DiagramItem::TF_Right, DiagramItem::TF_Left);
    QVERIFY(path.getStartItem() == startItem);
    QVERIFY(path.getEndItem() == endItem);
    QVERIFY(path.isEnabled());
    QVERIFY(path.flags() & QGraphicsItem::ItemIsSelectable);
}

void TestDiagramPath::testQuadFunction()
{
    // 测试 quad 函数（假设它是静态或可访问）
    // 由于 quad 是私有成员函数，我们通过路径状态间接测试
    DiagramPath path(startItem, endItem, DiagramItem::TF_Right, DiagramItem::TF_Left);
    path.updatePath(); // 触发内部计算
    // 无法直接验证 quad，但确保不崩溃
    QVERIFY(true);
}

void TestDiagramPath::testGetStartEndItem()
{
    DiagramPath path(startItem, endItem, DiagramItem::TF_Top, DiagramItem::TF_Bottom);
    QCOMPARE(path.getStartItem(), startItem);
    QCOMPARE(path.getEndItem(), endItem);
}

QTEST_MAIN(TestDiagramPath)
