class TestDeleteCommand : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testConstructor();
    void testRedo();
    void testUndo();
    void testRedoUndoSequence();

private:
    QGraphicsScene *scene;
    QGraphicsRectItem *item;
    DeleteCommand *command;
};

void TestDeleteCommand::initTestCase()
{
    scene = new QGraphicsScene();
    item = new QGraphicsRectItem(0, 0, 100, 50);
    scene->addItem(item);
    QVERIFY(scene->items().contains(item));
}

void TestDeleteCommand::cleanupTestCase()
{
    delete command;
    delete scene;
}

void TestDeleteCommand::testConstructor()
{
    command = new DeleteCommand(item, scene);
    QVERIFY(command != nullptr);
    QCOMPARE(command->scene(), scene);
    QCOMPARE(command->item(), item);
}

void TestDeleteCommand::testRedo()
{
    command = new DeleteCommand(item, scene);
    QVERIFY(scene->items().contains(item));
    command->redo();
    QVERIFY(!scene->items().contains(item));
}

void TestDeleteCommand::testUndo()
{
    command = new DeleteCommand(item, scene);
    command->redo();
    QVERIFY(!scene->items().contains(item));
    command->undo();
    QVERIFY(scene->items().contains(item));
    QCOMPARE(item->pos(), QPointF(0, 0));
}

void TestDeleteCommand::testRedoUndoSequence()
{
    command = new DeleteCommand(item, scene);
    QVERIFY(scene->items().contains(item));
    command->redo();
    QVERIFY(!scene->items().contains(item));
    command->undo();
    QVERIFY(scene->items().contains(item));
    command->redo();
    QVERIFY(!scene->items().contains(item));
}

QTEST_MAIN(TestDeleteCommand)
