void TestDiagramItem::initTestCase()
{
    scene = new QGraphicsScene();
    dummyMenu = new QMenu();
}

void TestDiagramItem::cleanupTestCase()
{
    delete scene;
    delete dummyMenu;
}

void TestDiagramItem::testConstructor()
{
    DiagramItem item(DiagramItem::Step, dummyMenu);
    QVERIFY(item.diagramType() == DiagramItem::Step);
    QVERIFY(item.parentItem() == nullptr);
    // 检查标志是否设置
    QVERIFY(item.flags() & QGraphicsItem::ItemIsSelectable);
    QVERIFY(item.flags() & QGraphicsItem::ItemIsMovable);
}

void TestDiagramItem::testBoundingRect()
{
    DiagramItem item(DiagramItem::Step, dummyMenu);
    QRectF rect = item.boundingRect();
    QVERIFY(rect.isValid());
    // 初始大小应为 m_grapSize (150,100) 加上边框扩展
    QVERIFY(rect.width() > 150);
    QVERIFY(rect.height() > 100);
}

void TestDiagramItem::testSetBrush()
{
    DiagramItem item(DiagramItem::Step, dummyMenu);
    QColor newColor(Qt::red);
    item.setBrush(newColor);
    // 无法直接验证内部颜色，但可以调用而不崩溃
    QVERIFY(true);
}

void TestDiagramItem::testSetFixedSize()
{
    DiagramItem item(DiagramItem::Step, dummyMenu);
    QSizeF newSize(200, 150);
    item.setFixedSize(newSize);
    QRectF rect = item.boundingRect();
    // 大小应大致反映新尺寸（考虑旋转和边框）
    QVERIFY(rect.width() > 200);
    QVERIFY(rect.height() > 150);
}

void TestDiagramItem::testRotationAngle()
{
    DiagramItem item(DiagramItem::Step, dummyMenu);
    // 初始角度应为0
    QCOMPARE(item.rotationAngle(), 0.0);
    item.setRotationAngle(45.0);
    QCOMPARE(item.rotationAngle(), 45.0);
}

