class TestDeleteCommand : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testUndoRedo();

private:
    QGraphicsScene *scene;
    QGraphicsItem *testItem;
};

void TestDeleteCommand::initTestCase()
{
    scene = new QGraphicsScene();
    testItem = new QGraphicsRectItem(0, 0, 100, 100);
    scene->addItem(testItem);
}

void TestDeleteCommand::cleanupTestCase()
{
    delete scene; // 会删除子项
}

void TestDeleteCommand::testUndoRedo()
{
    // 初始场景应有1个项
    QCOMPARE(scene->items().count(), 1);

    DeleteCommand cmd(testItem, scene);

    // 执行 redo (删除)
    cmd.redo();
    QCOMPARE(scene->items().count(), 0);

    // 执行 undo (恢复)
    cmd.undo();
    QCOMPARE(scene->items().count(), 1);

    // 项应回到原位
    QCOMPARE(testItem->pos(), QPointF(0, 0));
}

QTEST_MAIN(TestDeleteCommand)
