class TestFindReplaceDialog : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testConstructor();
    void testFindSignal();
    void testReplaceSignal();
    void testReplaceAllSignal();

private:
    FindReplaceDialog *dialog;
};

void TestFindReplaceDialog::initTestCase()
{
    dialog = new FindReplaceDialog();
}

void TestFindReplaceDialog::cleanupTestCase()
{
    delete dialog;
}

void TestFindReplaceDialog::testConstructor()
{
    QVERIFY(dialog != nullptr);
    // 应包含三个按钮
    QList<QPushButton *> buttons = dialog->findChildren<QPushButton *>();
    QCOMPARE(buttons.size(), 3);
}

void TestFindReplaceDialog::testFindSignal()
{
    QSignalSpy spy(dialog, &FindReplaceDialog::findText);
    dialog->findLineEdit->setText("test");
    QTest::mouseClick(dialog->findButton, Qt::LeftButton);
    QCOMPARE(spy.count(), 1);
    QCOMPARE(spy.at(0).at(0).toString(), QString("test"));
}

void TestFindReplaceDialog::testReplaceSignal()
{
    QSignalSpy spy(dialog, &FindReplaceDialog::replaceText);
    dialog->findLineEdit->setText("old");
    dialog->replaceLineEdit->setText("new");
    QTest::mouseClick(dialog->replaceButton, Qt::LeftButton);
    QCOMPARE(spy.count(), 1);
    QCOMPARE(spy.at(0).at(0).toString(), QString("old"));
    QCOMPARE(spy.at(0).at(1).toString(), QString("new"));
}

void TestFindReplaceDialog::testReplaceAllSignal()
{
    QSignalSpy spy(dialog, &FindReplaceDialog::replaceAllText);
    dialog->findLineEdit->setText("old");
    dialog->replaceLineEdit->setText("new");
    QTest::mouseClick(dialog->replaceAllButton, Qt::LeftButton);
    QCOMPARE(spy.count(), 1);
    QCOMPARE(spy.at(0).at(0).toString(), QString("old"));
    QCOMPARE(spy.at(0).at(1).toString(), QString("new"));
}

QTEST_MAIN(TestFindReplaceDialog)
