#include <QtTest>
#include <QStyleOptionGraphicsItem>
#include <QPainter>
#include "../../arrow.h"
#include "../../diagramitem.h"

class TestArrow : public QObject
{
    Q_OBJECT

private slots:
    void testConstructor();
    void testBoundingRect();
    void testUpdatePosition();
    void testPaint_data();
    void testPaint();
};

void TestArrow::testConstructor()
{
    DiagramItem startItem(DiagramItem::Step);
    DiagramItem endItem(DiagramItem::Step);
    Arrow arrow(&startItem, &endItem);
    QVERIFY(arrow.myStartItem == &startItem);
    QVERIFY(arrow.myEndItem == &endItem);
    QVERIFY(arrow.flags() & QGraphicsItem::ItemIsSelectable);
    QCOMPARE(arrow.pen().color(), QColor(Qt::black));
    QCOMPARE(arrow.pen().width(), 2);
}

void TestArrow::testBoundingRect()
{
    DiagramItem startItem(DiagramItem::Step);
    DiagramItem endItem(DiagramItem::Step);
    startItem.setPos(0, 0);
    endItem.setPos(100, 100);
    Arrow arrow(&startItem, &endItem);
    arrow.updatePosition();
    QRectF rect = arrow.boundingRect();
    QVERIFY(rect.isValid());
    QVERIFY(rect.contains(arrow.line().p1()));
    QVERIFY(rect.contains(arrow.line().p2()));
    qreal extra = (arrow.pen().width() + 20) / 2.0;
    QVERIFY(rect.width() >= arrow.line().length() + 2 * extra - 0.001);
    QVERIFY(rect.height() >= arrow.line().length() + 2 * extra - 0.001);
}

void TestArrow::testUpdatePosition()
{
    DiagramItem startItem(DiagramItem::Step);
    DiagramItem endItem(DiagramItem::Step);
    startItem.setPos(10, 20);
    endItem.setPos(150, 250);
    Arrow arrow(&startItem, &endItem);
    arrow.updatePosition();
    QLineF line = arrow.line();
    QCOMPARE(line.p1(), QPointF(10, 20));
    QCOMPARE(line.p2(), QPointF(150, 250));
}

void TestArrow::testPaint_data()
{
    QTest::addColumn<bool>("selected");
    QTest::addColumn<QPointF>("startPos");
    QTest::addColumn<QPointF>("endPos");
    QTest::newRow("not selected, normal line") << false << QPointF(0, 0) << QPointF(100, 0);
    QTest::newRow("selected, diagonal line") << true << QPointF(0, 0) << QPointF(100, 100);
}

void TestArrow::testPaint()
{
    QFETCH(bool, selected);
    QFETCH(QPointF, startPos);
    QFETCH(QPointF, endPos);
    DiagramItem startItem(DiagramItem::Step);
    DiagramItem endItem(DiagramItem::Step);
    startItem.setPos(startPos);
    endItem.setPos(endPos);
    Arrow arrow(&startItem, &endItem);
    arrow.setSelected(selected);
    arrow.updatePosition();
    QImage image(200, 200, QImage::Format_ARGB32);
    image.fill(Qt::transparent);
    QPainter painter(&image);
    QStyleOptionGraphicsItem option;
    arrow.paint(&painter, &option, nullptr);
    QVERIFY(!image.isNull());
    bool hasPixels = false;
    for (int y = 0; y < image.height(); ++y) {
        for (int x = 0; x < image.width(); ++x) {
            if (qAlpha(image.pixel(x, y)) > 0) {
                hasPixels = true;
                break;
            }
        }
        if (hasPixels) break;
    }
    QVERIFY(hasPixels);
}

QTEST_MAIN(TestArrow)
#include "test_arrow.moc"
