#include <QtTest>
#include <QStyleOptionGraphicsItem>
#include "arrow.h"
#include "diagramitem.h"

class TestArrow : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testConstructor();
    void testBoundingRect();
    void testUpdatePosition();
    void testPaint();

private:
    DiagramItem *startItem;
    DiagramItem *endItem;
    Arrow *arrow;
};

void TestArrow::initTestCase()
{
    // 创建两个 DiagramItem 作为起点和终点
    startItem = new DiagramItem(DiagramItem::Step);
    endItem = new DiagramItem(DiagramItem::Step);
    startItem->setPos(0, 0);
    endItem->setPos(100, 100);
    arrow = new Arrow(startItem, endItem);
}

void TestArrow::cleanupTestCase()
{
    delete arrow;
    delete startItem;
    delete endItem;
}

void TestArrow::testConstructor()
{
    QVERIFY(arrow != nullptr);
    QCOMPARE(arrow->myStartItem, startItem);
    QCOMPARE(arrow->myEndItem, endItem);
    QVERIFY(arrow->flags() & QGraphicsItem::ItemIsSelectable);
}

void TestArrow::testBoundingRect()
{
    QRectF rect = arrow->boundingRect();
    QVERIFY(rect.isValid());
    QVERIFY(!rect.isEmpty());
    // 边界矩形应包含线
    QLineF line = arrow->line();
    QVERIFY(rect.contains(line.p1()));
    QVERIFY(rect.contains(line.p2()));
}

void TestArrow::testUpdatePosition()
{
    // 移动终点项并更新箭头位置
    QPointF newPos(200, 200);
    endItem->setPos(newPos);
    arrow->updatePosition();
    QLineF updatedLine = arrow->line();
    // 检查线是否更新以反映新位置（注意：mapFromItem 转换）
    // 由于 mapFromItem 可能涉及场景坐标，我们主要验证线已更改
    QVERIFY(updatedLine.p2() != QPointF(0, 0)); // 不应为原点
}

void TestArrow::testPaint()
{
    // 测试 paint 函数不崩溃
    // 创建一个 QImage 和 QPainter 来模拟绘制
    QImage image(300, 300, QImage::Format_ARGB32);
    image.fill(Qt::white);
    QPainter painter(&image);
    QStyleOptionGraphicsItem option;
    // 调用 paint，验证它不会崩溃
    // 注意：paint 依赖于 myStartItem 和 myEndItem 的碰撞检测和 polygon
    // 由于我们创建了有效的 DiagramItem，这应该安全
    arrow->paint(&painter, &option, nullptr);
    QVERIFY(true); // 如果到达这里，paint 没有崩溃
}

QTEST_MAIN(TestArrow)
#include "test_arrow.moc"
