#include <QtTest>
#include <QStyleOptionGraphicsItem>
#include <QPainter>
#include "../../arrow.h"
#include "../../diagramitem.h"

class TestArrow : public QObject
{
    Q_OBJECT

private slots:
    void testConstructor();
    void testBoundingRect();
    void testUpdatePosition();
    void testPaintDoesNotCrash();
};

void TestArrow::testConstructor()
{
    // 由于 DiagramItem 构造可能需要场景，我们创建一个简单的测试来验证 Arrow 对象能被构造。
    // 这是一个冒烟测试。在实际项目中，需要确保 DiagramItem 可以被正确模拟或构造。
    DiagramItem startItem(DiagramItem::Step);
    DiagramItem endItem(DiagramItem::Step);
    Arrow arrow(&startItem, &endItem);
    QVERIFY(arrow.myStartItem == &startItem);
    QVERIFY(arrow.myEndItem == &endItem);
    QVERIFY(arrow.isSelectable());
}

void TestArrow::testBoundingRect()
{
    DiagramItem startItem(DiagramItem::Step);
    DiagramItem endItem(DiagramItem::Step);
    startItem.setPos(0, 0);
    endItem.setPos(100, 100);
    Arrow arrow(&startItem, &endItem);
    arrow.updatePosition(); // 确保线被更新
    QRectF rect = arrow.boundingRect();
    QVERIFY(rect.isValid());
    // 边界矩形应包含线，并有一些额外的边距
    QVERIFY(rect.contains(arrow.line().p1()));
    QVERIFY(rect.contains(arrow.line().p2()));
}

void TestArrow::testUpdatePosition()
{
    DiagramItem startItem(DiagramItem::Step);
    DiagramItem endItem(DiagramItem::Step);
    startItem.setPos(10, 20);
    endItem.setPos(30, 40);
    Arrow arrow(&startItem, &endItem);
    // 初始线可能为默认值 (0,0 -> 0,0)，调用 updatePosition 后应更新
    arrow.updatePosition();
    QLineF line = arrow.line();
    // 注意：mapFromItem 转换可能导致点不为精确的 (10,20) 和 (30,40)，但方向应一致
    // 我们检查线长度是否大致正确
    qreal expectedLength = QLineF(startItem.pos(), endItem.pos()).length();
    qreal actualLength = line.length();
    QVERIFY(qAbs(actualLength - expectedLength) < 1.0);
}

void TestArrow::testPaintDoesNotCrash()
{
    // 此测试旨在确保 paint 函数不会在简单场景下崩溃。
    // 我们创建一个 QImage 和 QPainter 来模拟绘制。
    DiagramItem startItem(DiagramItem::Step);
    DiagramItem endItem(DiagramItem::Step);
    startItem.setPos(0, 0);
    endItem.setPos(50, 0); // 放在不碰撞的位置
    Arrow arrow(&startItem, &endItem);
    arrow.updatePosition();
    QImage image(100, 100, QImage::Format_ARGB32);
    image.fill(Qt::white);
    QPainter painter(&image);
    QStyleOptionGraphicsItem option;
    arrow.paint(&painter, &option, nullptr);
    // 如果没有崩溃，测试通过
    QVERIFY(true);
}

QTEST_MAIN(TestArrow)
#include "test_arrow.moc"
