#include <QtTest>
#include <QStyleOptionGraphicsItem>
#include <QPixmap>
#include <QPainter>
#include "arrow.h"
#include "diagramitem.h"

class TestArrow : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testConstructor();
    void testUpdatePosition();
    void testBoundingRect();
    void testShape();
    void testPaint_data();
    void testPaint();

private:
    DiagramItem *startItem = nullptr;
    DiagramItem *endItem = nullptr;
    Arrow *arrow = nullptr;
};

void TestArrow::initTestCase()
{
    // 创建两个 DiagramItem 作为箭头的起点和终点
    startItem = new DiagramItem(DiagramItem::Step, nullptr);
    endItem = new DiagramItem(DiagramItem::Step, nullptr);
    startItem->setPos(0, 0);
    endItem->setPos(100, 100);
    arrow = new Arrow(startItem, endItem);
}

void TestArrow::cleanupTestCase()
{
    delete arrow;
    delete startItem;
    delete endItem;
}

void TestArrow::testConstructor()
{
    QVERIFY(arrow != nullptr);
    QCOMPARE(arrow->startItem(), startItem);
    QCOMPARE(arrow->endItem(), endItem);
    QVERIFY(arrow->flags() & QGraphicsItem::ItemIsSelectable);
}

void TestArrow::testUpdatePosition()
{
    // 移动终点项，然后更新箭头位置
    QPointF newEndPos(200, 50);
    endItem->setPos(newEndPos);
    arrow->updatePosition();
    // 检查箭头线是否大致指向新的终点方向（由于箭头计算，线可能不直接连接中心）
    // 我们主要验证 updatePosition 被调用后没有崩溃，且 line() 被设置
    QVERIFY(!arrow->line().isNull());
}

void TestArrow::testBoundingRect()
{
    QRectF bRect = arrow->boundingRect();
    QVERIFY(bRect.isValid());
    // 边界矩形应包含箭头线
    QLineF l = arrow->line();
    QVERIFY(bRect.contains(l.p1()));
    QVERIFY(bRect.contains(l.p2()));
    // 由于额外的边距，边界矩形应比单纯线矩形大
    QRectF lineRect(l.p1(), l.p2());
    lineRect = lineRect.normalized();
    QVERIFY(bRect.width() > lineRect.width() || bRect.height() > lineRect.height());
}

void TestArrow::testShape()
{
    QPainterPath shape = arrow->shape();
    QVERIFY(!shape.isEmpty());
    // 形状应包含箭头线（来自基类）和箭头头
    QVERIFY(shape.elementCount() > 0);
}

void TestArrow::testPaint_data()
{
    QTest::addColumn<bool>("selected");
    QTest::newRow("not selected") << false;
    QTest::newRow("selected") << true;
}

void TestArrow::testPaint()
{
    QFETCH(bool, selected);
    arrow->setSelected(selected);
    // 创建一个像素图和一个画家，尝试绘制箭头
    // 这主要验证 paint 函数不会在给定条件下崩溃
    QPixmap pixmap(300, 300);
    pixmap.fill(Qt::transparent);
    QPainter painter(&pixmap);
    QStyleOptionGraphicsItem option;
    arrow->paint(&painter, &option, nullptr);
    // 如果没有崩溃，测试通过
    QVERIFY(true);
}

QTEST_MAIN(TestArrow)
#include "test_arrow.moc"
