#include <QtTest>
#include <QPainter>
// Enable access to protected members
#define protected public
#define private public
#include "../../arrow.h"
#include "../../diagramitem.h"

class TestArrow : public QObject
{
    Q_OBJECT

private slots:
    void initTestCase();
    void cleanupTestCase();
    void testConstructor();
    void testBoundingRect();
    void testUpdatePosition();
    void testPaint();

private:
    DiagramItem *startItem;
    DiagramItem *endItem;
    Arrow *arrow;
};

void TestArrow::initTestCase()
{
    // 创建两个 DiagramItem 作为起点和终点
    startItem = new DiagramItem(DiagramItem::Step, nullptr);
    endItem = new DiagramItem(DiagramItem::Step, nullptr);
    startItem->setPos(0, 0);
    endItem->setPos(100, 100);

    // 创建 Arrow 对象
    arrow = new Arrow(startItem, endItem);
}

void TestArrow::cleanupTestCase()
{
    delete arrow;
    delete startItem;
    delete endItem;
}

void TestArrow::testConstructor()
{
    QVERIFY(arrow != nullptr);
    QVERIFY(arrow->startItem() == startItem);
    QVERIFY(arrow->endItem() == endItem);
    QVERIFY(arrow->isSelectable());
}

void TestArrow::testBoundingRect()
{
    QRectF rect = arrow->boundingRect();
    QVERIFY(rect.isValid());
    QVERIFY(!rect.isEmpty());

    // 检查边界矩形是否包含线条
    QLineF line = arrow->line();
    QVERIFY(rect.contains(line.p1()));
    QVERIFY(rect.contains(line.p2()));
}

void TestArrow::testUpdatePosition()
{
    // 移动终点项
    endItem->setPos(200, 200);
    arrow->updatePosition();

    QLineF line = arrow->line();
    QCOMPARE(line.p1(), QPointF(0, 0));
    QCOMPARE(line.p2(), QPointF(200, 200));
}

void TestArrow::testPaint()
{
    // 测试 paint 函数的基本调用
    // 由于 paint 函数依赖于 QPainter 和图形上下文，
    // 这里主要测试函数不会崩溃
    QVERIFY(arrow != nullptr);

    // 可以测试 arrowHead 多边形是否被正确计算
    QPolygonF head = arrow->arrowHead;
    // 在 updatePosition 或 paint 被调用后，arrowHead 应该被填充
    // 这里我们手动调用 updatePosition 来触发计算
    arrow->updatePosition();
    // 注意：arrowHead 是私有成员，无法直接访问。
    // 实际测试中可能需要将 arrowHead 设为 protected 或添加访问器。
    // 这里仅作为占位测试，确保测试框架运行。
    QVERIFY(true);
}

QTEST_MAIN(TestArrow)
#include "test_arrow.moc"
