#include <QtTest>
// Enable access to protected members
#define protected public
#define private public
#include "arrow.h"
#include "diagramitem.h"

class TestArrow : public QObject
{
    Q_OBJECT

private slots:
    void testConstructor();
    void testBoundingRect();
    void testUpdatePosition();
    void testPaint();
};

void TestArrow::testConstructor()
{
    DiagramItem startItem(DiagramItem::Step, nullptr);
    DiagramItem endItem(DiagramItem::Step, nullptr);
    Arrow arrow(&startItem, &endItem);
    QVERIFY(arrow.startItem() == &startItem);
    QVERIFY(arrow.endItem() == &endItem);
    QVERIFY(arrow.flags() & QGraphicsItem::ItemIsSelectable);
    QCOMPARE(arrow.pen().color(), arrowQt::black);
    QCOMPARE(arrow.pen().width(), 2);
}

void TestArrow::testBoundingRect()
{
    DiagramItem startItem(DiagramItem::Step, nullptr);
    DiagramItem endItem(DiagramItem::Step, nullptr);
    startItem.setPos(0, 0);
    endItem.setPos(100, 100);
    Arrow arrow(&startItem, &endItem);
    arrow.updatePosition();
    QRectF rect = arrow.boundingRect();
    QVERIFY(rect.isValid());
    QVERIFY(rect.contains(arrow.line().p1()));
    QVERIFY(rect.contains(arrow.line().p2()));
    qreal extra = (arrow.pen().width() + 20) / 2.0;
    QVERIFY(rect.width() >= arrow.line().length() + 2 * extra - 0.1);
    QVERIFY(rect.height() >= arrow.line().length() + 2 * extra - 0.1);
}

void TestArrow::testUpdatePosition()
{
    DiagramItem startItem(DiagramItem::Step, nullptr);
    DiagramItem endItem(DiagramItem::Step, nullptr);
    startItem.setPos(10, 20);
    endItem.setPos(150, 250);
    Arrow arrow(&startItem, &endItem);
    QLineF initialLine = arrow.line();
    arrow.updatePosition();
    QLineF updatedLine = arrow.line();
    QVERIFY(updatedLine != initialLine);
    QCOMPARE(updatedLine.p1(), arrow.mapFromItem(&startItem, 0, 0));
    QCOMPARE(updatedLine.p2(), arrow.mapFromItem(&endItem, 0, 0));
}

void TestArrow::testPaint()
{
    DiagramItem startItem(DiagramItem::Step, nullptr);
    DiagramItem endItem(DiagramItem::Step, nullptr);
    startItem.setPos(0, 0);
    endItem.setPos(200, 0);
    Arrow arrow(&startItem, &endItem);
    arrow.updatePosition();
    QVERIFY(!arrow.shape().isEmpty());
    QVERIFY(arrow.arrowHead.size() == 3);
    QVERIFY(arrow.isSelected() == false);
    arrow.setSelected(true);
    QVERIFY(arrow.isSelected() == true);
}

QTEST_MAIN(TestArrow)
#include "test_arrow.moc"
