#include <QtTest>
// Enable access to protected members
#define protected public
#define private public
#include "diagrampath.h"

class TestDiagramPath : public QObject
{
    Q_OBJECT

private slots:
    void testQuadFunction();
    void testConstructor();
    void testUpdatePath();
    void testDrawHead();
};

void TestDiagramPath::testQuadFunction()
{
    DiagramPath path(nullptr, nullptr, DiagramItem::TransformState::Top, DiagramItem::TransformState::Bottom);

    QPointF startPoint(0, 0);
    QPointF endPoint1(10, 10);
    QCOMPARE(path.quad(startPoint, endPoint1), 1);

    QPointF endPoint2(-10, 10);
    QCOMPARE(path.quad(startPoint, endPoint2), 4);

    QPointF endPoint3(-10, -10);
    QCOMPARE(path.quad(startPoint, endPoint3), 4);

    QPointF endPoint4(10, -10);
    QCOMPARE(path.quad(startPoint, endPoint4), 1);
}

void TestDiagramPath::testConstructor()
{
    DiagramItem *startItem = nullptr;
    DiagramItem *endItem = nullptr;
    DiagramItem::TransformState startState = DiagramItem::TransformState::Top;
    DiagramItem::TransformState endState = DiagramItem::TransformState::Bottom;

    DiagramPath path(startItem, endItem, startState, endState);

    QVERIFY(path.startItem == startItem);
    QVERIFY(path.endItem == endItem);
    QVERIFY(path.startState == startState);
    QVERIFY(path.endState == endState);
    QVERIFY(path.flags() & QGraphicsItem::ItemIsSelectable);
}

void TestDiagramPath::testUpdatePath()
{
    DiagramItem *startItem = nullptr;
    DiagramItem *endItem = nullptr;
    DiagramItem::TransformState startState = DiagramItem::TransformState::Top;
    DiagramItem::TransformState endState = DiagramItem::TransformState::Bottom;

    DiagramPath path(startItem, endItem, startState, endState);

    path.updatePath();

    QVERIFY(!path.path().isEmpty());
}

void TestDiagramPath::testDrawHead()
{
    DiagramItem *startItem = nullptr;
    DiagramItem *endItem = nullptr;
    DiagramItem::TransformState startState = DiagramItem::TransformState::Top;
    DiagramItem::TransformState endState = DiagramItem::TransformState::Bottom;

    DiagramPath path(startItem, endItem, startState, endState);

    QPointF endpoint(100, 100);
    QPointF endRectPoint1(100, 80);
    QPointF endRectPoint2(100, 120);
    QPointF endRectPoint3(80, 100);
    QPointF endRectPoint4(120, 100);

    path.drawHead(endpoint, endRectPoint1);
    path.drawHead(endpoint, endRectPoint2);
    path.drawHead(endpoint, endRectPoint3);
    path.drawHead(endpoint, endRectPoint4);

    QVERIFY(!path.path().isEmpty());
}

QTEST_MAIN(TestDiagramPath)
#include "test_diagrampath.moc"
