#include <QtTest>
// Enable access to protected members
#define protected public
#define private public
#include "../../debug/diagramitem.h"

class TestDiagramItem : public QObject
{
    Q_OBJECT

private slots:
    void testConstructor();
    void testBoundingRect();
    void testSetBrush();
    void testSetFixedSize();
    void testDiagramTypeEnum();
};

void TestDiagramItem::testConstructor()
{
    DiagramItem item(DiagramItem::StartEnd, nullptr);
    QVERIFY(item.boundingRect().isValid());
    QCOMPARE(item.diagramType(), DiagramItem::StartEnd);
}

void TestDiagramItem::testBoundingRect()
{
    DiagramItem item(DiagramItem::Conditional, nullptr);
    QRectF rect = item.boundingRect();
    QVERIFY(rect.width() > 0);
    QVERIFY(rect.height() > 0);
    QVERIFY(!rect.isNull());
}

void TestDiagramItem::testSetBrush()
{
    DiagramItem item(DiagramItem::Step, nullptr);
    QColor newColor(Qt::red);
    item.setBrush(newColor);
    QCOMPARE(item.brushColor(), newColor);
}

void TestDiagramItem::testSetFixedSize()
{
    DiagramItem item(DiagramItem::Io, nullptr);
    QSizeF newSize(200, 150);
    item.setFixedSize(newSize);
    QCOMPARE(item.size(), newSize);
}

void TestDiagramItem::testDiagramTypeEnum()
{
    QCOMPARE(static_cast<int>(DiagramItem::StartEnd), 0);
    QCOMPARE(static_cast<int>(DiagramItem::Conditional), 1);
    QCOMPARE(static_cast<int>(DiagramItem::Step), 2);
    QCOMPARE(static_cast<int>(DiagramItem::Io), 3);
}

QTEST_MAIN(TestDiagramItem)
#include "test_diagramitem.moc"
